# [659] Split Array into Consecutive Subsequences
#

from typing import List

# @lc code=start
class Solution:
    '''O(n^2), but in practice better than n^2
    '''
    def isPossible(self, nums: List[int]) -> bool:
        lists = []
        # build the lists
        for num in nums:
            idx = None
            remove = []
            for i in reversed(range(len(lists))):  # greedy: the later (later) built lists first
                if idx is None and lists[i][-1] == num - 1:
                    idx = i
                if lists[i][-1] < num - 1:   # useless list, i.e., no hope for growing
                    if len(lists[i]) < 3:
                        return False         # early stop
                    else:
                        remove.append(i)
            if idx is not None:
                lists[idx].append(num)
            else:
                lists.append([num])
            for idx in remove:
                lists.pop(i)
            
        # check if length >= 3
        for lst in lists:
            if len(lst) < 3:
                return False
        return True

from collections import Counter

class Solution:
    '''O(n), a clever way using two Counters.
       when creating a new subsequence, already make sure that it is a valid subsequence when length larger equal than 3.
    '''
    def isPossible(self, nums: List[int]) -> bool:
        counter = Counter(nums)
        subseq = Counter()  # last number in a subsequence -> number of subsequences
        for num in nums:
            if counter[num] == 0:
                continue
            if subseq[num-1] > 0:
                counter[num] -= 1
                subseq[num-1] -= 1
                subseq[num] += 1
                continue
            if counter[num+1] > 0 and counter[num+2] > 0:  # start a new (aleady valid) subseq
                counter[num] -= 1
                counter[num+1] -= 1
                counter[num+2] -= 1
                subseq[num+2] += 1
                continue
            return False
        return True



